import React from 'react';
import Typing from 'react-typing-animation';
import Navbar from './components/Navbar';
import Services from './components/Services';
import Features from './components/Features';
import Descriptions from './components/Descriptions';
import Pricing from './components/Pricing';
import Team from './components/Team';
import Process from './components/Process';
import Testi from './components/Testi';
import Started from './components/Started';
import Contact from './components/Contact';
import SocialMedia from './components/SocialMedia';
import Footer from './components/Footer';
import FooterLinks from './components/FooterLinks';
import Blog from './components/Blog';
import Switcher from './components/Switcher';
import { Link } from 'react-router-dom';
import Aux from './hoc/Aux_';
import StarfieldAnimation from 'react-starfield-animation';

class Home extends React.Component {
  render() {
    var bkg1 = {
      backgroundImage: 'url(images/wave-shape/wave1.png)'
    };
    var bkg2 = {
      backgroundImage: 'url(images/wave-shape/wave2.png)'
    };
    var bkg3 = {
      backgroundImage: 'url(images/wave-shape/wave3.png)'
    };

    return (
      <Aux>
        {/* Navbar Component*/}
        <Navbar />

        <div bg-home>\

          <StarfieldAnimation
            numParticles={1000}
            depth={1}
            style={{
              position: 'fixed',
              width: '100vw',
              height: '100vh'
            }}
          />
        </div>

    <section className="section bg-home home-half" id="home">
  <canvas id="canvas1"></canvas>

          <div className="bg-overlay"/>

          <div className="display-table">
            <div className="display-table-cell">
              <div className="container">
                <div className="row">
                  <div className="col-lg-8 offset-lg-2 text-white text-center  zoom">

                  <img class="home-icon " src="/images/home/Group 2.png"/>
                    <p className="padding-t-15 home-desc">
                      You have the next great idea to present to the world. Explore the world through IEEE
                      <br />
                      <br />
                        <br />
                          <br />
                    </p>

                  </div>
                </div>
              </div>
            </div>
          </div>
          <div className="wave-effect wave-anim">
            <div className="waves-shape shape-one">
              <div className="wave wave-one" style={bkg1}/>
            </div>
            <div className="waves-shape shape-two">
              <div className="wave wave-two" style={bkg2}/>
            </div>
            <div className="waves-shape shape-three">
              <div className="wave wave-three" style={bkg3}/>
            </div>
          </div>
        </section>

        {/* Services Component*/}
        <Services />

        {/* Features Component*/}
        <Features />

        {/* Descriptions Component*/}
        <Descriptions />

        {/* Pricing Component*/}
        <Pricing />

        {/* Blog Component*/}
        <Blog />


        {/* Team Component*/}
        <Team />



        {/* Testi Component*/}
        <Testi />



        {/* Started Component*/}
        <Started />

        {/* Process Component*/}
        <Process />

        {/* Contact Component*/}
        <Contact />

        {/* SocialMedia Component*/}
        <SocialMedia />

        {/* Footer Component*/}
        <Footer />

        {/* FooterLinks Component*/}
        <FooterLinks />

        {/* Switcher Component*/}

      </Aux>
    );
  }
}
const image = new Image();
image.src ="/images/home/Group 2.png"
image.addEventListener('load', function(){
    const canvas = document.getElementById('canvas1');
    const ctx = canvas.getContext('2d');
    canvas.width = 572;
    canvas.height = 352;

    let particlesArray = [];
    const numberOfParticles = 12000;
    const detail = 1;

    ctx.drawImage(image, 0, 0, canvas.width, canvas.height);
    const pixels = ctx.getImageData(0, 0, canvas.width, canvas.height);
    ctx.clearRect(0, 0, canvas.width, canvas.height);

    let grid = [];
    for (let y = 0; y < canvas.height; y += detail){
        let row = [];
        for (let x = 0; x < canvas.width; x += detail){
            const red = pixels.data[(y * 4 * pixels.width) + (x * 4)]
            const green = pixels.data[(y * 4 * pixels.width) + (x * 4 + 1)]
            const blue = pixels.data[(y * 4 * pixels.width) + (x * 4 + 2)]
            const color = 'rgb(' + red +',' + green + ',' + blue + ')';
            const brightness = calculateBrightness(red, green, blue)/100;

            row.push(brightness);
        }
        grid.push(row);
    }

    class Particle {
        constructor(){
            this.x = Math.random() * canvas.width;
            this.y =  0;
            //this.prevX = this.x;
            this.speed = 0;
            this.velocity = Math.random() * 0.7;
            this.size = Math.random() * 2 + .1;
            this.state = {
                  x : 0,
                  y : 0,
                  speed : 0,
                  velocity : 0,
                  size : 0,
  };
        }
        update () {
            this.speed = grid[Math.floor(this.y / detail)][Math.floor(this.x / detail)];
            let movement = (2.5 - this.speed) + this.velocity;
            this.y += movement;
            if (this.y >= canvas.height) {
                this.y = 0;
                this.x = Math.random() * canvas.width;
            }
            //console.log(this.x += movement)
        }
        draw(){
            ctx.beginPath();
            ctx.fillStyle = 'white';
            ctx.arc(this.x, this.y, this.size, 0, 2 * Math.PI);
            ctx.fill();
        }
    }

    function init(){
        for (let i = 0; i < numberOfParticles; i++){
            particlesArray.push(new Particle());
        }
    }
    init();

    function animate () {
        ctx.globalAlpha = 0.05;
        ctx.fillStyle = 'rgb(0, 0,0)';
        ctx.fillRect(0, 0, canvas.width, canvas.height);
        ctx.globalAlpha = 0.2;
        for (let i = 0; i < particlesArray.length; i++) {
            particlesArray[i].update();
            ctx.globalAlpha = particlesArray[i].speed * 0.3;
            particlesArray[i].draw();
        }
        requestAnimationFrame( animate );
    }
    animate();

    function calculateBrightness(red, green, blue){
        return Math.sqrt(
            (red * red) * 0.299 +
            (green * green) * 0.587 +
            (blue * blue) * 0.114
        );
    }

});

export default Home;
